nt(\"Sample deposit record:\")\n                print(\"  ID: \" .. first_deposit.id)\n                print(\"  Sender: \" .. first_deposit.sender)\n                print(\"  Amount: \" .. first_deposit.amount)\n                print(\"  Vote: \" .. first_deposit.vote_choice)\n                print(\"  Case: \" .. (first_deposit.case_id or \"none\"))\n            end\n        else\n            history_valid = false\n        end\n        \n        if history_valid and history_count == 4 then\n            print(\"‚úì Deposit history tracking working correctly\")\n        else\n            print(\"‚úó Deposit history tracking failed\")\n        end\n        \n        return history_valid and history_count == 4\n    end,\n    \n    -- Test pool statistics calculation\n    test_pool_statistics = function()\n        print(\"\\n=== Testing Pool Statistics ===\")\n        \n        local pool_stats = getPoolStatistics()\n        \n        print(\"Pool Statistics:\")\n        print(\"  Total balance: \" .. pool_stats.balances.total)\n        print(\"  True percentage: \" .. string.format(\"%.1f\", pool_stats.percentages.true_percentage) .. \"%\")\n        print(\"  Fake percentage: \" .. string.format(\"%.1f\", pool_stats.percentages.fake_percentage) .. \"%\")\n        print(\"  Deposit count: \" .. pool_stats.metadata.deposit_count)\n        \n        -- Validate statistics\n        local expected_true_pct = 75.0  -- 3 out of 4 deposits\n        local expected_fake_pct = 25.0  -- 1 out of 4 deposits\n        \n        local stats_correct = math.abs(pool_stats.percentages.true_percentage - expected_true_pct) < 0.1 and\n                             math.abs(pool_stats.percentages.fake_percentage - expected_fake_pct) < 0.1 and\n                             pool_stats.metadata.deposit_count == 4\n        \n        if stats_correct then\n            print(\"‚úì Pool statistics calculation correct\")\n        else\n            print(\"‚úó Pool statistics calculation failed\")\n        end\n        \n        return stats_correct\n    end,\n    \n    -- Test consistency validation\n    test_consistency_validation = function()\n        print(\"\\n=== Testing Consistency Validation ===\")\n        \n        -- First ensure voting stats match (simulate them)\n        State.voting_stats.total_deposits = State.pool.total_balance\n        State.voting_stats.true_deposited = State.pool.true_pool\n        State.voting_stats.fake_deposited = State.pool.fake_pool\n        \n        local consistency_check = validateDepositConsistency()\n        print(\"Consistency check result: \" .. tostring(consistency_check))\n        \n        -- Test inconsistency detection\n        local original_voting_total = State.voting_stats.total_deposits\n        State.voting_stats.total_deposits = \"5000000000\"  -- Intentional mismatch\n        \n        local inconsistency_detected = not validateDepositConsistency()\n        print(\"Inconsistency detection: \" .. tostring(inconsistency_detected))\n        \n        -- Restore consistency\n        State.voting_stats.total_deposits = original_voting_total\n        \n        local final_consistency = validateDepositConsistency()\n        \n        if consistency_check and inconsistency_detected and final_consistency then\n            print(\"‚úì Consistency validation working correctly\")\n        else\n            print(\"‚úó Consistency validation failed\")\n        end\n        \n        return consistency_check and inconsistency_detected and final_consistency\n    end,\n    \n    -- Test deposit history filtering and pagination\n    test_history_filtering = function()\n        print(\"\\n=== Testing History Filtering & Pagination ===\")\n        \n        -- Test filtering by vote choice\n        local true_deposits = 0\n        local fake_deposits = 0\n        \n        for _, deposit in ipairs(State.deposit_history) do\n            if deposit.vote_choice == \"true\" then\n                true_deposits = true_deposits + 1\n            elseif deposit.vote_choice == \"fake\" then\n                fake_deposits = fake_deposits + 1\n            end\n        end\n        \n        print(\"True deposits in history: \" .. true_deposits .. \" (expected: 3)\")\n        print(\"Fake deposits in history: \" .. fake_deposits .. \" (expected: 1)\")\n        \n        local filtering_correct = true_deposits == 3 and fake_deposits == 1\n        \n        -- Test pagination (simulate)\n        local total_history = #State.deposit_history\n        local limit = 2\n        local offset = 1\n        \n        local paginated_count = math.min(limit, total_history - offset)\n        print(\"Pagination test - Total: \" .. total_history .. \", Limit: \" .. limit .. \", Offset: \" .. offset)\n        print(\"Expected paginated count: \" .. paginated_count)\n        \n        if filtering_correct and paginated_count > 0 then\n            print(\"‚úì History filtering and pagination logic correct\")\n        else\n            print(\"‚úó History filtering or pagination failed\")\n        end\n        \n        return filtering_correct\n    end,\n    \n    -- Test memory management (deposit history limit)\n    test_memory_management = function()\n        print(\"\\n=== Testing Memory Management ===\")\n        \n        -- Save original history\n        local original_history = {}\n        for i, deposit in ipairs(State.deposit_history) do\n            original_history[i] = deposit\n        end\n        \n        -- Simulate many deposits (exceed 1000 limit)\n        local initial_count = #State.deposit_history\n        \n        -- Add many test deposits to exceed limit\n        for i = 1, 1005 do\n            updatePoolBalance(\"1000000000\", \"test_user_\" .. i, \"true\", \"test_tx_\" .. i)\n        end\n        \n        local final_count = #State.deposit_history\n        print(\"Initial history count: \" .. initial_count)\n        print(\"After adding 1005 deposits: \" .. final_count)\n        print(\"Memory limit working: \" .. tostring(final_count <= 1000))\n        \n        -- Restore original history for other tests\n        State.deposit_history = original_history\n        -- Restore original pool state\n        State.pool.total_balance = \"4000000000\"\n        State.pool.true_pool = \"3000000000\"\n        State.pool.fake_pool = \"1000000000\"\n        State.pool.deposit_count = 4\n        \n        local memory_management_working = final_count <= 1000\n        \n        if memory_management_working then\n            print(\"‚úì Memory management working correctly\")\n        else\n            print(\"‚úó Memory management failed\")\n        end\n        \n        return memory_management_working\n    end\n}\n\n-- Test large number arithmetic\nlocal function testLargeNumberArithmetic()\n    print(\"\\n=== Testing Large Number Arithmetic ===\")\n    \n    -- Test adding very large USDA amounts\n    local large_amount_1 = \"999999999999999999\"  -- ~1 billion USDA\n    local large_amount_2 = \"888888888888888888\"  -- ~888 million USDA\n    \n    local sum = addStringNumbers(large_amount_1, large_amount_2)\n    local expected_sum = \"1888888888888888887\"  -- Manual calculation\n    \n    print(\"Large number addition test:\")\n    print(\"  Amount 1: \" .. large_amount_1)\n    print(\"  Amount 2: \" .. large_amount_2)\n    print(\"  Sum: \" .. sum)\n    print(\"  Expected: \" .. expected_sum)\n    \n    local arithmetic_correct = sum == expected_sum\n    \n    if arithmetic_correct then\n        print(\"‚úì Large number arithmetic working correctly\")\n    else\n        print(\"‚úó Large number arithmetic failed\")\n    end\n    \n    return arithmetic_correct\nend\n\n-- Test LiquidOps preparation\nlocal function testLiquidOpsPreparation()\n    print(\"\\n=== Testing LiquidOps Preparation ===\")\n    \n    -- Check if pool structure is ready for LiquidOps integration\n    local pool_structure_ready = State.pool.total_balance ~= nil and\n                